        /// <summary>
        /// Obtener una lista de objetos de la entidad Currency a partir del código de producto.
        /// </summary>
        /// <param name="productId">
        /// Identificador de Producto cuyos currency relacionados se desea obtener.
        /// </param>
        /// <returns>
        /// Lista de objetos Currency.
        /// </returns>
        public BusinessCollection GetCurrencyListByProductId(int productId)
        {
            BusinessCollection resultList = new BusinessCollection();

            foreach (ProductCurrency productCurrency in this.ProductCurrencyList)
            {
                if (productCurrency.ProductId == productId)
                {
                    PrimaryKey currencyKey = COMMEN.Currency.CreatePrimaryKey(productCurrency.CurrencyCode);
                    if (this.CurrencyList.Contains(currencyKey))
                    {
                        resultList.Add(this.CurrencyList[currencyKey]);
                    }
                }
            }

            return resultList;
        }

        public BusinessCollection GetProductListByCurrency(COMMEN.Currency currency)
        {
            BusinessCollection resultList = new BusinessCollection();

            foreach (ProductCurrency productCurrency in this.ProductCurrencyList)
            {
                if (productCurrency.CurrencyCode == currency.CurrencyCode)
                {
                    PrimaryKey productKey = Product.Entities.Product.CreatePrimaryKey(productCurrency.ProductId);
                    if (this.ProductList.Contains(productKey))
                    {
                        resultList.Add(this.ProductList[productKey]);
                    }
                }
            }

            return resultList;
        }

